import React from 'react';
import { formatDate } from '../utils/formatDate';

const FileCard = ({ file, onDownload, onDelete }) => {
  return (
    <div className="bg-white shadow-md rounded-lg p-4 mb-4">
      <h3 className="text-lg font-semibold">{file.title}</h3>
      <p className="text-gray-600">{file.description}</p>
      <p className="text-sm text-gray-500">Uploaded by: {file.uploaded_by}</p>
      <p className="text-sm text-gray-500">Date: {formatDate(file.uploaded_at)}</p>
      <p className="text-sm text-gray-500">Size: {file.size} bytes</p>
      <div className="mt-2 flex space-x-2">
        <button onClick={() => onDownload(file.id)} className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600">Download</button>
        <button onClick={() => onDelete(file.id)} className="bg-red-500 text-white px-4 py-2 rounded hover:bg-red-600">Delete</button>
      </div>
    </div>
  );
};

export default FileCard;
